//Sign up page
import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';

import * as ROUTES from '../../constants/routes';
import { withFirebase } from '../Firebase';
import { compose } from 'recompose';

const SignUpPage = () => (
  <div>
    <h1>SignUp</h1>
  <SignUpForm />
  </div>
);

const INITIAL_STATE = {
  username: '',
  email: '',
  passwordOne: '',
  passwordTwo: '',
  nativelang: '',
  learnlang: '',
  age: '',
  error: null,
};

class SignUpFormBase extends Component {
  constructor(props) {
    super(props);

    this.state = {...INITIAL_STATE};
  }
  onSubmit = event => {
    const { username, email, passwordOne, nativelang, learnlang, age} = this.state;
    this.props.firebase
      .doCreateUserWithEmailAndPassword(email, passwordOne)
      .then(authUser => {
        // Create a user in your Firebase realtime database
        return this.props.firebase
          .user(authUser.user.uid)
          .set({
          username,
          email,
 	  nativelang,
	  learnlang,
	  age,
          });
      })
	.then(() => {
        this.setState({ ...INITIAL_STATE });
        this.props.history.push(ROUTES.HOME);
      })
      .catch(error => {
        this.setState({ error });
      });
    event.preventDefault();
  }
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

handleOptionChange = changeEvent => {
  this.setState({
    selectedOption: changeEvent.target.value
  });
};

  render() {
    const {
      username,
      email,
      passwordOne,
      passwordTwo,
	  nativelang,
	  learnlang,
	age,
      error,
    } = this.state;
	
   const isInvalid =
        passwordOne !== passwordTwo ||
      	passwordOne === '' ||
      	email === ''||
      	username === ''||
	nativelang === ''||
 	learnlang === '';




    return (
      <form onSubmit={this.onSubmit}>
        <input
          name="username"
          value={username}
          onChange={this.onChange}
          type="text"
          placeholder="Username"
        />
        <input
          name="email"
          value={email}
          onChange={this.onChange}
          type="text"
          placeholder="Email Address"
        />
        <input
          name="passwordOne"
          value={passwordOne}
          onChange={this.onChange}
          type="password"
          placeholder="Password"
        />
        <input
          name="passwordTwo"
          value={passwordTwo}
          onChange={this.onChange}
          type="password"
          placeholder="Confirm Password"
        />

<br/>
<br/>
		
         <p>What language do you speak fluently?</p> 
        <select name="nativelang" defaultValue ={nativelang} onChange={this.onChange}>
	<option value="" disabled selected>I am fluent in</option>
      	<option value="Arabic">Arabic</option>
	<option value="English">English</option>
        <option value="German">German</option>
        <option value="Hindi">Hindi</option>
         <option value="Irish">Irish</option>
         <option value="Spanish">Spanish</option>
	<option value="Welsh">Welsh</option>
          </select>
        
 <br/>       
<br/>

	<p>  What language would you like to practice?</p>
        <select name="learnlang" defaultValue ={learnlang} onChange={this.onChange}>
	<option value="" disabled selected>I will speak</option>
      	<option value="Arabic">Arabic</option>
	<option value="English">English</option>
        <option value="German">German</option>
        <option value="Hindi">Hindi</option>
         <option value="Irish">Irish</option>
         <option value="Spanish">Spanish</option>
	<option value="Welsh">Welsh</option>
          </select>
       
<br/>
<br/>
<p> Lastly, we need to make sure you are over 18 to use our app:</p>

   <div className="form-check">
    <label>
      <input
        type="radio"
        name="react-tips"
        value="option1"
        checked={this.state.selectedOption === "option1"}
        onChange={this.handleOptionChange}
        className="form-check-input"
      />
      Yes my childhood is behind me
    </label>
  </div>

  <div className="form-check">
    <label>
      <input
        type="radio"
        name="react-tips"
        value="option2"
        checked={this.state.selectedOption === "option2"}
        onChange={this.handleOptionChange}
        className="form-check-input"
      />
      Option 2
    </label>
  </div>

<br/>

        <button disabled={isInvalid} type="submit">Sign Up</button>
        {error && <p>{error.message}</p>}

      </form>
    );

  }
}
const SignUpLink = () => (
  <p>
    Don't have an account? <Link to={ROUTES.SIGN_UP}>Sign Up</Link>
  </p>
);

const SignUpForm = compose(
  withRouter,
  withFirebase,
)(SignUpFormBase);

export default SignUpPage;
export { SignUpForm, SignUpLink };
