//Sign up page
import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';

import * as ROUTES from '../../constants/routes';
import { withFirebase } from '../Firebase';
import { compose } from 'recompose';

const SignUpPage = () => (
  <div>
    <h1>SignUp</h1>
  <SignUpForm />
  </div>
);

const INITIAL_STATE = {
  username: '',
  email: '',
  passwordOne: '',
  passwordTwo: '',
  nativelang: '',
  learnlang: '',
  error: null,
};
const calculate_age = (dob1) => {
    var today = new Date();
    var birthDate = new Date(dob1);  // create a date object directly from `dob1` argument
    var age_now = today.getFullYear() - birthDate.getFullYear();
    var m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) 
    {
        age_now--;
    }
    console.log(age_now);
    return age_now;
  }

const handleChange_age = (event) => {
    console.log("DOB:", event.target.value);

    this.setState({ dob1: event.target.value }, () => {
      // example of setState callback
      // this will have the latest this.state.dob1
      console.log(this.state.dob1);
    })

    // call calculate_age with event.target.value
    var age_latest = {age_latest: this.calculate_age(event.target.value)}
    console.log(age_latest);

    this.setState({ age1: age_latest }, () => {
      // this will have the latest this.state.age1
      console.log("Age:", this.state.age1);
    })
  }

class SignUpFormBase extends Component {
  constructor(props) {
    super(props);

    this.state = {...INITIAL_STATE};
  }
  onSubmit = event => {
    const { username, email, passwordOne, nativelang, learnlang } = this.state;
    this.props.firebase
      .doCreateUserWithEmailAndPassword(email, passwordOne)
      .then(authUser => {
        // Create a user in your Firebase realtime database
        return this.props.firebase
          .user(authUser.user.uid)
          .set({
          username,
          email,
 	  nativelang,
	  learnlang,
          });
      })
	.then(() => {
        this.setState({ ...INITIAL_STATE });
        this.props.history.push(ROUTES.HOME);
      })
      .catch(error => {
        this.setState({ error });
      });
    event.preventDefault();
  }
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };
  render() {
    const {
      username,
      email,
      passwordOne,
      passwordTwo,
	  nativelang,
	  learnlang,
      error,
    } = this.state;
	
   const isInvalid =
      //passwordOne !== passwordTwo ;
      //passwordOne === '' ||
      //email === '' ||
      //username === ''||
 	learnlang
	
	

    return (
      <form onSubmit={this.onSubmit}>
        <input
          name="username"
          value={username}
          onChange={this.onChange}
          type="text"
          placeholder="Username"
        />
        <input
          name="email"
          value={email}
          onChange={this.onChange}
          type="text"
          placeholder="Email Address"
        />
        <input
          name="passwordOne"
          value={passwordOne}
          onChange={this.onChange}
          type="password"
          placeholder="Password"
        />
        <input
          name="passwordTwo"
          value={passwordTwo}
          onChange={this.onChange}
          type="password"
          placeholder="Confirm Password"
        />
<br/>
<br/>
	<input 
	type="date" 
	name="date_of_birth" 
	//defaultValue= {dob1} 
	onChange={this.handleChange_age}
	/>

<br/>
<br/>
		
         What is your native language?
         <select name = "nativelang" id="native">
	<option value="x">I Speak</option>
	<option value="Arabic">Arabic</option>
	<option value="English">English</option>
        <option value="German">German</option>
        <option value="Hindi">Hindi</option>
         <option value="Irish">Irish</option>
         <option value="Spanish">Spanish</option>
	<option value="Welsh">Welsh</option>
          </select>
        
        
<br/>

	  What language would you like to practice?
        <select value ={learnlang} id="learn" defaultValue={this.state.selectValue} 
 onChange={this.handleChange} >
      <option value="Arabic">Arabic</option>
	<option value="English">English</option>
        <option value="German">German</option>
        <option value="Hindi">Hindi</option>
         <option value="Irish">Irish</option>
         <option value="Spanish">Spanish</option>
	<option value="Welsh">Welsh</option>
          </select>
       
<br/>
	

<br/>
        <button disabled={isInvalid} type="submit">Sign Up</button>
        {error && <p>{error.message}</p>}
      </form>
    );

  }
}
const SignUpLink = () => (
  <p>
    Don't have an account? <Link to={ROUTES.SIGN_UP}>Sign Up</Link>
  </p>
);

const SignUpForm = compose(
  withRouter,
  withFirebase,
)(SignUpFormBase);

export default SignUpPage;
export { SignUpForm, SignUpLink };
