//Sign up page
import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';

import * as ROUTES from '../../constants/routes';
import { withFirebase } from '../Firebase';
import { compose } from 'recompose';

const SignUpPage = () => (
  <div>
    <h1>SignUp</h1>
  <SignUpForm />
  </div>
);

const INITIAL_STATE = {
  username: '',
  email: '',
  passwordOne: '',
  passwordTwo: '',
  nativelang: '',
  learnlang: '',
  age: '',
  error: null,
};

class SignUpFormBase extends Component {
  constructor(props) {
    super(props);

    this.state = {...INITIAL_STATE};
  }
  onSubmit = event => {
    const { username, email, passwordOne, nativelang, learnlang, age} = this.state;
    this.props.firebase
      .doCreateUserWithEmailAndPassword(email, passwordOne)
      .then(authUser => {
        // Create a user in your Firebase realtime database including the following info
        return this.props.firebase
          .user(authUser.user.uid)
          .set({
          username,
          email,
 	  nativelang,
	  learnlang,
	  age,
          });
      })
	.then(() => {
        this.setState({ ...INITIAL_STATE });
        this.props.history.push(ROUTES.HOME);
      })
      .catch(error => {
        this.setState({ error });
      });
    event.preventDefault();
  }
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    const {
      username,
      email,
      passwordOne,
      passwordTwo,
	nativelang,
	learnlang,
	age,
      error,
    } = this.state;
{/*function to invalidate submit button if any of the follwing are true*/}
   const isInvalid =
        //passwordOne !== passwordTwo ||
      	//passwordOne === '' ||
      	//email === ''||
      	//username === ''||
	//nativelang === ''||
 	//learnlang === ''||
	age === '';




    
return (
<div>
      <form onSubmit={this.onSubmit}>
{/*username input box*/}
        <input 
          name="username"
          value={username}
          onChange={this.onChange}
          type="text"
          placeholder="Username"
        />
{/*email address input box*/}
        <input 
          name="email"
          value={email}
          onChange={this.onChange}
          type="text"
          placeholder="Email Address"
        />
{/*both password input boxes*/}
        <input
          name="passwordOne"
          value={passwordOne}
          onChange={this.onChange}
          type="password"
          placeholder="Password"
        />
        <input
          name="passwordTwo"
          value={passwordTwo}
          onChange={this.onChange}
          type="password"
          placeholder="Confirm Password"
        />

<br/>
<br/>

{/*What language do you speak fluently*/}
<div>		
         <p>What language do you speak fluently?</p> 
        <select name="nativelang" defaultValue ={nativelang} onChange={this.onChange}>
	<option value="" disabled selected>I am fluent in</option>
      	<option value="Arabic">Arabic</option>
	<option value="English">English</option>
        <option value="German">German</option>
        <option value="Hindi">Hindi</option>
         <option value="Irish">Irish</option>
         <option value="Spanish">Spanish</option>
	<option value="Welsh">Welsh</option>
          </select>
        </div>
 <br/>       
<br/>
{/*What language would you like to practice*/}
	<p>  What language would you like to practice?</p>
        <select name="learnlang" defaultValue ={learnlang} onChange={this.onChange}>
	<option value="" disabled selected>I will speak</option>
      	<option value="Arabic">Arabic</option>
	<option value="English">English</option>
        <option value="German">German</option>
        <option value="Hindi">Hindi</option>
         <option value="Irish">Irish</option>
         <option value="Spanish">Spanish</option>
	<option value="Welsh">Welsh</option>
          </select>
       
<br/>
<br/>

{/*over 18 slider*/}
<p> Lastly, we need to make sure you are over 18 to use our app:</p>

<p> Yes I am over 18</p>
<label class="switch">
  <input 
   name ="age" 
   value={age}
   onChange={this.onChange}
   type="checkbox"
   />
  <span class="slider round"></span>
</label>

<br/>

{/*submit button*/}
        <button disabled={isInvalid} type="submit">Sign Up</button>
        {error && <p>{error.message}</p>}

      </form>
</div>
    );

  }
}
const SignUpLink = () => (
  <p>
    Don't have an account? <Link to={ROUTES.SIGN_UP}>Sign Up</Link>
  </p>
);

const SignUpForm = compose(
  withRouter,
  withFirebase,
)(SignUpFormBase);

export default SignUpPage;
export { SignUpForm, SignUpLink };
